-- Table: migrations
CREATE TABLE "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);


-- Table: users
CREATE TABLE "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);


-- Table: password_reset_tokens
CREATE TABLE "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));


-- Table: sessions
CREATE TABLE "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));


-- Table: cache
CREATE TABLE "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));


-- Table: cache_locks
CREATE TABLE "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));


-- Table: jobs
CREATE TABLE "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);


-- Table: job_batches
CREATE TABLE "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));


-- Table: failed_jobs
CREATE TABLE "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);


-- Table: inventories
CREATE TABLE "inventories" ("id" integer primary key autoincrement not null, "item_id" varchar not null, "item_name" varchar not null, "description" text, "category" varchar not null, "stock" integer not null default '0', "reorder_level" integer not null default '10', "unit_price" numeric not null, "supplier" varchar, "location" varchar, "status" varchar check ("status" in ('active', 'inactive', 'discontinued')) not null default 'active', "created_at" datetime, "updated_at" datetime);


-- Table: reports
CREATE TABLE "reports" ("report_id" integer primary key autoincrement not null, "report_type" varchar check ("report_type" in ('daily_usage', 'monthly_procurement', 'low_stock_alert', 'reconciliation', 'forecast')) not null, "generated_date" datetime not null, "report_date" date not null, "data_summary" text not null, "forecast_period" integer, "forecast_value" numeric, "confidence_level" numeric, "generated_by" varchar, "created_at" datetime, "updated_at" datetime);


-- Table: archives
CREATE TABLE "archives" ("archive_id" integer primary key autoincrement not null, "entity_type" varchar not null, "entity_id" integer not null, "action" varchar not null, "old_data" text, "new_data" text, "user_id" varchar, "reference_number" varchar, "notes" text, "archived_date" datetime not null, "created_at" datetime, "updated_at" datetime);


-- Table: reservations
CREATE TABLE "reservations" ("reservation_id" integer primary key autoincrement not null, "item_id" varchar not null, "quantity" integer not null, "status" varchar check ("status" in ('pending', 'approved', 'rejected', 'completed', 'cancelled')) not null default 'pending', "job_order_number" varchar, "requested_by" varchar not null, "approved_by" varchar, "requested_date" datetime not null, "approved_date" datetime, "expires_at" datetime, "notes" text, "created_at" datetime, "updated_at" datetime, "priority_level" integer not null default '3', "is_urgent" tinyint(1) not null default '0', "estimated_completion" datetime, foreign key("item_id") references "inventories"("item_id") on delete cascade);


-- Table: stock_transactions
CREATE TABLE "stock_transactions" ("transaction_id" integer primary key autoincrement not null, "item_id" varchar not null, "transaction_type" varchar check ("transaction_type" in ('procurement', 'sale', 'reservation', 'return', 'damage', 'adjustment')) not null, "quantity" integer not null, "previous_stock" integer not null, "new_stock" integer not null, "reference_number" varchar, "notes" text, "created_by" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("item_id") references "inventories"("item_id") on delete cascade);


-- Table: alerts
CREATE TABLE "alerts" ("id" integer primary key autoincrement not null, "item_id" varchar not null, "item_name" varchar not null, "current_stock" integer not null, "reorder_level" integer not null, "category" varchar not null, "supplier" varchar, "urgency" varchar check ("urgency" in ('low', 'medium', 'high', 'critical')) not null default 'medium', "alert_type" varchar check ("alert_type" in ('low_stock', 'out_of_stock', 'expiry', 'reorder')) not null default 'low_stock', "message" text, "acknowledged" tinyint(1) not null default '0', "acknowledged_by" varchar, "acknowledged_at" datetime, "created_at" datetime, "updated_at" datetime);


